import { useContext } from 'react';
import { useEffect } from 'react';
import { FormEvent, useState } from 'react';
import { FoodContext } from '../../context/FoodContext';
import { Container, ModalDiv, ModalContent } from './styles'
interface IFood {
    id: string;
    foodName: string;
    foodDescription: string;
    foodPrice: number;
    foodIimage: string;
  }

export function ModalEditFood() {
    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [description, setDescription] = useState('');
    const [image, setImage] = useState('');
    const { handleEditModalClose, foods } = useContext(FoodContext)

    async function handleAddUpdated(event: FormEvent) {
        event.preventDefault()
        foods.map((food: IFood) => {
            if(food.foodName === name){
                food.foodIimage = image
                food.foodDescription = description
                food.foodPrice = price
            }
            return food
        })


        localStorage.setItem('foods', JSON.stringify(foods));

        handleEditModalClose()
    }



    return (
        <Container>
            <ModalDiv>
                <button onClick={handleEditModalClose}>Close</button>
            </ModalDiv>
            <ModalContent>
                <h2>Cadastrar Prato</h2>
                <input value={image} onChange={event => setImage(event.target.value)} placeholder="Url da Imagem" />
                <input value={name} onChange={event => setName(event.target.value)} placeholder="Nome" />
                <input value={description} onChange={event => setDescription(event.target.value)} placeholder="Descrição" />
                <input value={price} onChange={event => setPrice(Number(event.target.value))} placeholder="Valor" />
                <button type="submit" onClick={handleAddUpdated}>Editar</button>
            </ModalContent>
        </Container>
    );
}
