import Header from '../../components/Header';
import FoodCard from '../../components/FoodCard';
import { FoodsContainer } from './styles';
import { useState } from 'react';


interface IFood {
  id: string;
  foodName: string;
  foodDescription: string;
  foodPrice: number;
  foodIimage: string;
}

export default function FoodList() {
  const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [description, setDescription] = useState('');
    const [image, setImage] = useState('');
  let object = JSON.parse(localStorage.getItem('foods')!);
   function handleDelete(id: string) {
    let filtered = object.foods.filter((food: IFood) => food.id !== id);
    localStorage.setItem('foods', JSON.stringify(filtered))
    object.foods.push({
      id:new Date(),
      foodName: name,
      foodPrice: price,
      foodDescription: description,
      foodIimage: image
  })
  }
  return (
    <>
      <Header />
      <FoodsContainer >
        {
          object?.foods?.map((food: IFood) => (
            <FoodCard key={food.id}
              image={food.foodIimage}
              name={food.foodName}
              id={food.id}
              description={food.foodDescription}
              price={food.foodPrice}
              handleDelete={handleDelete} />
          ))}
      </FoodsContainer>
    </>
  );
}
