import styled from 'styled-components';

export const Container = styled.div`
  background: #ace5ee;
  padding: 30px 0;
   `;
export const Div= styled.div`
    width: 1280px;
    margin: 0 auto;
    padding: 0 0 160px;
    display: flex;
    align-items: center;
    justify-content: space-between;
 `;

export const Image = styled.img`
width: 200px;
height: 200px ;
`;

 export const Button = styled.button`
  font-weight: 600;
          border-radius: 8px;
          border: 0;
          background: #39b100;
          color: #fff;
          display: flex;
          flex-direction: row;
          align-items: center;
 `;
