import styled from 'styled-components';

export const Container = styled.div`
width: 100%;
height: 100vh;
position: absolute;
top: 0;
left: 0;
z-index: 10;
background-color: rgba(0,0,0, 0.8);
display: flex;
align-items: center;
justify-content: center;
`;

export const ModalDiv = styled.div`
background-color: #fff;
width: 60%;
height: 60%;
border-radius: 20px;

button{
    background-color: transparent;
    display: flex;
    align-items: center;
    position: relative;
    top: 16px;
    outline: none;
    right: calc(-100% + 85px);
    border: none;
    color: #000;
    width: 32px;
    height: 32px;
}
`

export const ModalContent = styled.div`

`
