import Header from '../../components/Header';
import FoodCard from '../../components/FoodCard';
import { FoodsContainer } from './styles';
import { useState } from 'react';


interface IFood {
  id: string;
  foodName: string;
  foodDescription: string;
  foodPrice: number;
  foodIimage: string;
}

export default function FoodList() {
  const [foods, setFoods] = useState<IFood[]>(JSON.parse(localStorage.getItem('foods')!) ?? []);

  function handleDelete(id: string) {
    const filtered = foods.filter((food: IFood) => food.id !== id);

    localStorage.setItem('foods', JSON.stringify(filtered))

    setFoods(filtered);
  }

  return (
    <>
      <Header />
      <FoodsContainer >
        {foods.map((food: IFood) => (
          <FoodCard key={food.id}
            image={food.foodIimage}
            name={food.foodName}
            id={food.id}
            description={food.foodDescription}
            price={food.foodPrice}
            handleDelete={handleDelete} />
        ))}
      </FoodsContainer>
    </>
  );
}
