import { useParams } from "react-router-dom";
import {Container} from './styles';

interface IFood {
    id: string;
    foodName: string;
    foodDescription: string;
    foodPrice: number;
    foodIimage: string;
  }

export function FoodPage(){
    let object = JSON.parse(localStorage.getItem('foods')!);
    const { id } = useParams<{ id: string }>();
  const selectedFood = object.foods.find(
    (food: IFood) => food.id === id
  );
  if(id === selectedFood){
    <Container>
    <h1>{selectedFood?.foodName}</h1>
    </Container>
  } else{
    return <h2>falhou</h2>
  }

}
