import { useEffect } from "react";
import { useContext } from "react";
import { useHistory, useParams } from "react-router-dom";
import { FoodContext } from "../../context/FoodContext";
import {Container, HeaderDiv, BannerDiv} from './styles';

interface IFood {
    id: string;
    foodName: string;
    foodDescription: string;
    foodPrice: number;
    foodIimage: string;
  }

export function FoodPage(){
  const  history = useHistory();
  const {foods} = useContext(FoodContext)
    const { id } = useParams<{ id: string }>();
  const selectedFood = foods.find((food: IFood) => food.id === id);
  useEffect(() =>{
    if(!selectedFood){
      history.push("/")
    } 
  },[history, selectedFood])
  return(
    <Container>
      <HeaderDiv>
      <h1>{selectedFood?.foodName}</h1>
      </HeaderDiv>
      <BannerDiv>
    <img width="100%" height="200px" src={selectedFood?.foodIimage}/>
    </BannerDiv>
    <p>{selectedFood?.foodDescription}</p>
    <span>{selectedFood?.foodPrice}</span>
    </Container>
  )
}
